import React from 'react';
import { useNavigate, useParams } from 'react-router-dom';

interface InvestorData {
  id?: number;
  first_name: string;
  last_name: string;
  date_of_birth: string;
  phone_number: string;
  street_address: string;
  state: string;
  zip_code: string;
}

const InvestorForm: React.FC = () => {
  const navigate = useNavigate();
  const { id } = useParams<{ id: string }>();
  const isEdit = Boolean(id);
  const [formDataState, setFormDataState] = React.useState<InvestorData>({
    first_name: '',
    last_name: '',
    date_of_birth: '',
    phone_number: '',
    street_address: '',
    state: '',
    zip_code: ''
  });

  // On mount, if editing, fetch existing investor
  React.useEffect(() => {
    console.log("id", id);
    if (isEdit && id !== "investors") {
      fetch(`http://localhost:3000/api/investors/${id}`)
        .then(res => res.json())
        .then(data => setFormDataState(data));
    }
  }, [id, isEdit]);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormDataState({ ...formDataState, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const url = isEdit ?
      `http://localhost:3000/api/investors/${id}` :
      'http://localhost:3000/api/investors';
    const method = isEdit ? 'PUT' : 'POST';

    const res = await fetch(url, {
      method,
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ investor: formDataState })
    });

    if (res.ok) {
      navigate('/');
    } else {
      const error = await res.json();
      alert('Error: ' + (error.errors || error.message));
    }
  };

  return (
    <form onSubmit={handleSubmit} noValidate>
      <div>
        <label>First Name:</label>
        <input
          name="first_name"
          type="text"
          required
          value={formDataState.first_name}
          onChange={handleChange}
        />
      </div>
      <div>
        <label>Last Name:</label>
        <input
          name="last_name"
          type="text"
          required
          value={formDataState.last_name}
          onChange={handleChange}
        />
      </div>
      <div>
        <label>Date of Birth:</label>
        <input
          name="date_of_birth"
          type="date"
          required
          value={formDataState.date_of_birth}
          onChange={handleChange}
        />
      </div>
      <div>
        <label>Phone Number:</label>
        <input
          name="phone_number"
          type="tel"
          required
          value={formDataState.phone_number}
          onChange={handleChange}
        />
      </div>
      <div>
        <label>Street Address:</label>
        <input
          name="street_address"
          type="text"
          required
          value={formDataState.street_address}
          onChange={handleChange}
        />
      </div>
      <div>
        <label>State:</label>
        <input
          name="state"
          type="text"
          required
          value={formDataState.state}
          onChange={handleChange}
        />
      </div>
      <div>
        <label>Zip Code:</label>
        <input
          name="zip_code"
          type="text"
          required
          value={formDataState.zip_code}
          onChange={handleChange}
        />
      </div>
      <button type="submit">
        {isEdit ? 'Update Investor' : 'Add Investor'}
      </button>
    </form>
  );
};

export default InvestorForm;
